INSERT INTO `products` VALUES (28,'Áo Lumi ',6,12,20,20,20,220000.00,'Áo Lumi là lựa chọn lý tưởng cho những ai yêu sự nhẹ nhàng và nữ tính. Thiết kế nhấn với tay áo phồng bồng bềnh, cổ chữ V viền ren dịu dàng tôn vẻ thanh thoát. \r\nChi tiết:\r\n•	Dáng lửng, bo nhún linh hoạt ở eo\r\n•	Cổ V nữ tính, phối viền và lót ren bên trong\r\n•	Tay phồng nhẹ, bo chun ở cổ tay\r\n•	Vải cotton thêu siêu nhẹ, mát mẻ\r\n','[\"1765690731761-24618599.png\",\"1765690731762-346461857.png\",\"1765690731762-550243833.png\"]','2025-12-14 12:20:41','2025-12-14 12:38:51',0,220000),(29,'Áo nâu tay dài ',6,55,18,85,24,245000.00,'Áo nâu tay dài mang đến phong cách tối giản đầy ấn tượng nhờ kiểu dáng áo trễ vai độc đáo cùng chất liệu len co giãn. Thiết kế phối tay lạ mắt, cài nút trước, form ôm tôn dáng giúp nàng dễ dàng nổi bật mọi lúc.\r\nChi tiết:\r\n•	Áo trễ vai, phối lớp tay nổi bật\r\n•	Hàng nút cài dọc thân trước\r\n•	Chất liệu len dày dặn, mềm mại, co giãn thoải mái\r\n•	Dáng áo ôm thân, dễ phối riêng hoặc mặc layer cá tính\r\n','[\"1765689917689-798739061.webp\",\"1765689985239-523479651.webp\",\"1765689985241-510031555.webp\",\"1765689985243-267040879.webp\",\"1765689985244-691047723.webp\"]','2025-12-14 12:25:17','2025-12-14 12:26:25',0,245000),(30,'Áo hoa nhí',6,51,20,24,20,215000.00,'Áo hoa nhí mang phong cách vintage nhẹ nhàng, dễ mặc với họa tiết hoa nhí trên nền vải gân, điểm nhấn nút hình trái tim duyên dáng. Áo thiết kế dáng ôm, ngực rút nhún vừa vặn, đường viền ren nữ tính giúp tôn lên vẻ đẹp mềm mại và trẻ trung cho người mặc.\r\nChi tiết:\r\n•	Áo hai dây dáng ôm, ngực nhún nhẹ tạo điểm nhấn\r\n•	Dây mảnh có thể điều chỉnh, dễ chịu khi mặc\r\n•	Chất liệu thun gân cotton pha, họa tiết hoa nhí toàn thân\r\n•	Viền cổ và gấu áo viền ren nhẹ nhàng, mang hơi hướng vintage\r\n•	Co giãn tốt, ôm dáng thoải mái\r\n','[\"1765690120455-897270449.webp\",\"1765690120455-206618831.webp\",\"1765690120456-839454178.webp\",\"1765690120456-928352006.webp\"]','2025-12-14 12:28:40','2025-12-14 12:28:40',20,172000),(31,'Áo elena ',6,5,44,45,3,256000.00,'Áo elena phối hợp dáng sơ mi cổ bẻ cùng thiết kế vạt chéo trẻ trung trên nền vải thun be nhạt, nhẹ thoáng. Phần thân wrap tạo hiệu ứng xếp lớp mềm mại, chất thun mỏng giúp áo trở nên nhẹ nhàng, dễ phối đủ kiểu từ đi học, đi chơi đến công sở.\r\nChi tiết:\r\n•	Áo thun tay ngắn, cổ sơ mi basic\r\n•	Thiết kế vạt chéo, tạo hiệu ứng layer tinh tế\r\n•	Vải thun be nhạt nhẹ, thoáng mát, co giãn tốt\r\n•	Logo trước ngực nhỏ nổi bật\r\n•	Áo mỏng nhẹ, hiệu ứng xuyên thấu nhẹ nhàng, hiện đại\r\n','[\"1765690209590-4952696.webp\",\"1765690209591-4898897.webp\",\"1765690209591-680371127.webp\",\"1765690209592-146126335.webp\"]','2025-12-14 12:30:09','2025-12-14 21:54:58',30,179200),(32,'Áo mindy ',6,15,52,17,12,225000.00,'Áo mindy mang hơi thở đồng quê thanh lịch với cổ vuông, ngực nhún và dây rút điều chỉnh nữ tính.\r\nChi tiết:\r\n•	Áo lửng, eo nhún bo chun\r\n•	Phần ngực dáng nhún, dây rút điều chỉnh\r\n•	Tay phồng chun có dây buộc nơ\r\n•	Vải cotton pha nhẹ, thoáng mát\r\n•	Co giãn nhẹ, dễ dàng vận động\r\n','[\"1765690283072-953809917.webp\",\"1765690283072-467391926.webp\",\"1765690283073-664715190.webp\",\"1765690283073-20890575.webp\"]','2025-12-14 12:31:23','2025-12-14 12:31:23',0,225000),(33,'Áo Yeshi ',6,25,42,32,28,243000.00,'Áo Yeshi mang vẻ đẹp gợi cảm tinh tế với thiết kế baby tee ôm sát, tôn tối đa đường cong cơ thể. \r\nChi tiết:\r\n•	Dáng croptop lửng, ôm body cực chuẩn\r\n•	Cổ tròn basic, tay ngắn bó sát nhẹ\r\n•	Chất cotton co giãn 4 chiều mềm mại, mặc mát cả ngày\r\n•	Điểm nhấn dòng chữ “yeshi” đính đá lấp lánh chạy ngang ngực, vừa sang vừa nổi bật\r\n•	Phù hợp mix&match mọi kiểu: jeans, chân váy, layer áo khoác đều cực chất\r\n','[\"1765690361794-668505194.webp\",\"1765690361794-687187792.webp\",\"1765690361794-485840615.webp\",\"1765690361795-590452703.webp\"]','2025-12-14 12:32:41','2025-12-14 12:32:41',0,243000),(34,'Áo Yeshi Trắng ',6,35,52,71,12,219000.00,'Áo Yeshi Trắng mang vẻ đẹp thanh lị